mapper.select:


    r.del_flag = '0'
     and r.major_id = #{majorId}
     and r.course_id = #{courseId}
     and r.status = #{status}
     and r.uploader_id = #{uploaderId}
     and c.course_name like concat('%', #{courseName}, '%')
     and r.resource_name like concat('%', #{resourceName}, '%')
     and r.description like concat('%', #{description}, '%')

order by r.id desc

mapper.update:
update tb_course_resource
resource_name = #{resourceName}, resource_type = #{resourceType}, file_url = #{fileUrl}, file_hash = #{fileHash}, file_size = #{fileSize}, link_url = #{linkUrl}, description = #{description}, status = #{status}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}

mapper.select:


    r.del_flag = '0' and r.status = 1
     and r.major_id = #{majorId}
     and r.course_id = #{courseId}
     and ifnull(r.last_download_time, r.publish_time) >= #{fromTime}

order by r.download_count desc, r.id desc
 limit #{limit}
 limit 10
