mapper.select:


    ml.del_flag = '0'
    and ml.major_id = #{majorId}
    and ml.user_id = #{userId}

        and exists (
        select 1 from sys_user su
        where su.user_id = ml.user_id and su.user_name like concat('%', #{userName}, '%') and su.del_flag = '0'
        )


order by ml.id desc

mapper.delete:
delete
from tb_major_lead
where major_id = #{majorId}
  and user_id = #{userId}

mapper.select:
select count(1)
from tb_major_lead
where user_id = #{userId}

select count(1)
from tb_major_lead
where user_id = #{userId}
  and major_id = #{majorId}
  and del_flag = '0'
