mapper.select:
select *
from tb_survey_answer
where survey_id = #{surveyId}
  and user_id = #{userId}
  and del_flag = '0'
limit 1

mapper.update:
update tb_survey_answer
submit_time = #{submitTime}, update_time2 = #{updateTime2}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}
